import aps2.uLogging
import aps2.apsapi


class ResourceBase:
    def __init__(self):
        pass

    def provision(self):
        aps2.uLogging.logger.info("IN APS : provision")

    def unprovision(self):
        aps2.uLogging.logger.info("IN APS : unprovision")

    def retrieve(self):
        aps2.uLogging.logger.info("IN APS : retrieve")

    def configure(self, new):
        self.__dict__ = new.__dict__.copy()
        aps2.uLogging.logger.info("IN APS : configure")
