import aps2.uLogging
import aps2.apsapi


class ResourceBase:
    logger = aps2.uLogging.get_logger('/tmp/myapp.log', aps2.uLogging.DEBUG)

    def __init__(self):
        pass

    def provision(self):
        self.logger.info("IN APS : provision")

    def unprovision(self):
        self.logger.info("IN APS : unprovision")

    def retrieve(self):
        self.logger.info("IN APS : retrieve")

    def configure(self, new):
        self.__dict__ = new.__dict__.copy()
        self.logger.info("IN APS : configure")
