import aps2.uLogging
from aps2.apsapi import JsonNode

import sys
#sys.path.append('/var/www/html/pycloud/')
import aps2.apsapi

class ResourceBase(JsonNode):

    logger = aps2.uLogging.getLogger('/tmp/myapp.log', aps2.uLogging.DEBUG)

    def __init__(self):
        self.logger = aps2.uLogging.getLogger('/tmp/myapp.log', aps2.uLogging.DEBUG)

    def provision(self):
        self.logger.info("IN APS : provision")

    def unprovision(self):
        self.logger.info("IN APS : unprovision")

    def retrieve(self):
        self.logger.info("IN APS : retrieve")

    def configure(self, new):
        self.__dict__ = new.__dict__.copy()
        self.logger.info("IN APS : configure")
