import aps2.uLogging
import aps2.Info
import aps2.InfoParse

import pprint
import json


def print_environment():
    aps2.uLogging.trace('REQUEST ENVIRONMENT:')
    aps2.uLogging.trace(pprint.pformat(aps2.Info.Info.get_ENVIRON()))


def print_request_body():
    print_json("REQUEST BODY:", aps2.Info.Info.get_BODY_JSON())


def print_list_services(list_services):
    aps2.uLogging.trace("LIST_SERVICES:")
    aps2.uLogging.trace(list_services)


def print_json(prefix, injson):
    aps2.uLogging.trace(prefix + json.dumps(injson, indent=8))


def print_line():
    aps2.uLogging.info("-----------------------------------")

